import React, { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { fetchArticles } from "../actions/articles";

function Articles() {
  const articlesStore = useSelector(state => state.article);
  const dispatch = useDispatch();

  useEffect(
    () => {
      dispatch(fetchArticles());
    },
    [dispatch]
  );

  return (
    <div className="screen articles-container">
      <div className="screen articles-articles">
      {articlesStore.map(article => (
        <div key={article.id} className="article">{article.title}</div>
      ))}</div>
    </div>
  );
}

export default Articles;
