import React, { useState, useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { fetchArticles } from "../actions/articles";

function Articles() {
  const articlesStore = useSelector(state => state.article);
  const dispatch = useDispatch();
  const [openFilter, setOpenFilter] = useState(false);
  const [filters, setFilters] = useState({
    philosophy: false,
    comunism: false,
    drugs: false,
    ecomomy: false,
    history: false,
    general: false
  });

  useEffect(
    () => {
      dispatch(fetchArticles());
    },
    [dispatch]
  );
  function updateFilters(field, value) {
    debugger
    filters[field] = value;
    setFilters(filters);
  }
  return (
    <div className="screen articles-container">
      <div className="articles-articles">
        <div className="articles-header">
          <h1>Artículos</h1>
          <div
            className="articles-filter-button"
            onClick={() => setOpenFilter(!openFilter)}
          >
            <i className="fa fa-sliders" />
          </div>
        </div>
        <div
          className={`articles-filter articles-filter${!openFilter &&
            "--close"}`}
        >
          <div
            onClick={() => updateFilters("philosophy", filters.philosophy)}
            className={`articles-filter-item articles-filter-itemr${filters.philosophy &&
              "--selected"}`}
          >
            Filosofía
          </div>
          <div
            onClick={() => setFilters(...filters, !filters.philosophy)}
            className={`articles-filter-item articles-filter-itemr${filters.philosophy &&
              "--selected"}`}
          >
            Comunismo
          </div>
          <div
            onClick={() => setFilters(...filters, !filters.philosophy)}
            className={`articles-filter-item articles-filter-itemr${filters.philosophy &&
              "--selected"}`}
          >
            Drogas
          </div>
          <div
            onClick={() => setFilters(...filters, !filters.philosophy)}
            className={`articles-filter-item articles-filter-itemr${filters.philosophy &&
              "--selected"}`}
          >
            Historia
          </div>
          <div
            onClick={() => setFilters(...filters, !filters.philosophy)}
            className={`articles-filter-item articles-filter-itemr${filters.philosophy &&
              "--selected"}`}
          >
            Economía
          </div>
          <div
            onClick={() => setFilters(...filters, !filters.philosophy)}
            className={`articles-filter-item articles-filter-itemr${filters.philosophy &&
              "--selected"}`}
          >
            General
          </div>
        </div>
        {articlesStore.map(article => (
          <div key={article.id} className="articles-article">
            <img className="articles-article-img" src={article.images} />
            <div className="articles-articles-details">
              <h3 className="articles-article-title">{article.title}</h3>
              <p className="articles-article-body">
                {article.body && article.body.slice(0, 500).concat("... ")}
                <span>Segir Leyendo</span>
              </p>
              {article.tags.split(",").map(tag => (
                <span key={tag} className="articles-article-tags">{tag}</span>
              ))}
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default Articles;
