import React, { Fragment, useState } from "react";
import { Link } from "react-router-dom";
import Logo from "../assets/logo.png";
function Menu() {
const [openMenu, setOpenMenu] = useState(false);
  return (
    <Fragment>
      <div className={`menu-container menu-container${openMenu && "--close"}`}>
        <div className="menu-logo">
          <img className="menu-logo-img" src={Logo} alt="logo" />
        </div>
        <Link className="menu-item" to="/">
          <p className="menu-item-p">Home</p>
        </Link>
        <Link className="menu-item" to="/articles">
          <p className="menu-item-p">Articles</p>
        </Link>
      </div>
      <div className="mobile-layout">
        <div className="mobile-layout-title"> Antonio Escohotado</div>
        <div className="menu-icon" onClick={()=>setOpenMenu(!openMenu)} >
        <i className={`fa fa-${openMenu ? "times" : "bars"}`} />
        </div>
      </div>
    </Fragment>
  );
}

export default Menu;
